actionConfiguration.body:
() => {
		updateOrder.run(
			() => {
				showAlert('Added', 'success');
				closeModal('ItemModal');
				getOrder.run();
			},
			(err) => {
				showAlert(err, 'error');
				console.log(err);
			},
			{
				order: {
					...getOrder.data.order,
					item: [
						...getOrder.data.order.item,
						NetSuite.buildItem(
							ItemID.text,
							ItemQuantity.text,
							parseInt(ItemAmount.text) / 1.08,
							ItemLocation.text,
							parseInt(ItemDLVRLocation.selectedOptionValue || ItemDLVRLocation.options[0].value),
						)
					]
				}
			}
		)
	}

