actionConfiguration.body:
() => {
		updateOrder.run(
			() => {
				showAlert('Updated', 'success');
				getOrder.run();
			},
			(err) => {
				showAlert(err, 'error');
				console.log(err);
			},
			{
				order: {
					...getOrder.data.order,
					...NetSuite.buildCustomer(OrderPhone.text, OrderCustomerName.text),
					memo: OrderMemo.text,
					custbody_expecteddeliverytime: OrderExpectedDeliveryTime.selectedOptionValue,
					shipdate: OrderShipDate.formattedDate,
				}
			}
		)
	}

